import React from 'react';
import { useQuery, gql } from '@apollo/client';
import { useAuth } from '../utils/AuthContext';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { LogOut, User, Award, TrendingUp } from 'lucide-react';

const GET_USER_DATA = gql`
  query GetUserData {
    user {
      id
      login
      transactions(where: {type: {_eq: "xp"}}, order_by: {createdAt: asc}) {
        amount
        createdAt
        path
      }
      progresses(order_by: {createdAt: asc}) {
        grade
        createdAt
        path
      }
    }
  }
`;

const Profile = () => {
  const { logout } = useAuth();
  const { loading, error, data } = useQuery(GET_USER_DATA);

  if (loading) return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="animate-pulse text-2xl text-gray-600">Loading...</div>
    </div>
  );

  if (error) return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="text-2xl text-red-600">Error loading data</div>
    </div>
  );

  const user = data?.user[0];
  if (!user) return null;

  const xpData = user.transactions.map(tx => ({
    date: new Date(tx.createdAt).toLocaleDateString(),
    xp: tx.amount
  }));

  const totalXP = user.transactions.reduce((sum, tx) => sum + tx.amount, 0);
  const passedAudits = user.progresses.filter(p => p.grade > 0).length;
  const totalAudits = user.progresses.length;
  const auditRatio = totalAudits ? (passedAudits / totalAudits * 100).toFixed(1) : 0;

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 py-6 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Welcome, {user.login}</h1>
              <p className="text-gray-500 mt-1">Student Dashboard</p>
            </div>
            <button
              onClick={logout}
              className="flex items-center gap-2 bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg transition-colors"
            >
              <LogOut size={20} />
              Logout
            </button>
          </div>
        </div>

        {/* Stats Grid */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          {/* User Info Card */}
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center gap-4">
              <div className="bg-blue-100 p-3 rounded-full">
                <User className="h-6 w-6 text-blue-600" />
              </div>
              <div>
                <p className="text-sm font-medium text-gray-600">Student ID</p>
                <p className="text-xl font-semibold text-gray-900">{user.id}</p>
              </div>
            </div>
          </div>

          {/* Total XP Card */}
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center gap-4">
              <div className="bg-green-100 p-3 rounded-full">
                <TrendingUp className="h-6 w-6 text-green-600" />
              </div>
              <div>
                <p className="text-sm font-medium text-gray-600">Total XP</p>
                <p className="text-xl font-semibold text-gray-900">{totalXP.toLocaleString()}</p>
              </div>
            </div>
          </div>

          {/* Audit Success Rate Card */}
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center gap-4">
              <div className="bg-purple-100 p-3 rounded-full">
                <Award className="h-6 w-6 text-purple-600" />
              </div>
              <div>
                <p className="text-sm font-medium text-gray-600">Audit Success Rate</p>
                <div className="flex items-baseline gap-2">
                  <p className="text-xl font-semibold text-gray-900">{auditRatio}%</p>
                  <p className="text-sm text-gray-500">({passedAudits}/{totalAudits})</p>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Chart */}
        <div className="bg-white rounded-lg shadow-sm p-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">XP Progress Over Time</h2>
          <div className="h-[400px] w-full">
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={xpData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#E5E7EB" />
                <XAxis 
                  dataKey="date" 
                  stroke="#6B7280"
                  tick={{ fill: '#6B7280' }}
                />
                <YAxis 
                  stroke="#6B7280"
                  tick={{ fill: '#6B7280' }}
                />
                <Tooltip 
                  contentStyle={{ 
                    backgroundColor: '#FFF',
                    border: '1px solid #E5E7EB',
                    borderRadius: '6px'
                  }}
                />
                <Legend />
                <Line 
                  type="monotone" 
                  dataKey="xp" 
                  stroke="#6366F1" 
                  strokeWidth={2}
                  dot={{ fill: '#6366F1', strokeWidth: 2 }}
                  activeDot={{ r: 6 }}
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
