  navWrapper: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '0 1rem',
    width: '100%',
    display: 'flex',
    justifyContent: 'space-between'
  },
  brandContainer: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem'
  },
  brandIcon: {
    width: '32px',
    height: '32px',
    color: '#6b21a8'
  },
  brandText: {
    fontSize: '1.25rem',
    fontWeight: 'bold',
    background: 'linear-gradient(90deg, #6b21a8, #6366F1)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent'
  },
  userInfoContainer: {
    display: 'flex',
    alignItems: 'center',
    gap: '1rem'
  },
  logoutBtn: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    background: 'linear-gradient(90deg, #ef4444, #ec4899)',
    color: '#fff',
    border: 'none',
    padding: '0.6rem 1rem',
    borderRadius: '8px',
    cursor: 'pointer'
  },
  main: {
    paddingTop: '80px',
    paddingBottom: '1rem'
  },
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '0 1rem'
  },
  welcomeSection: {
    textAlign: 'center',
    marginBottom: '2rem'
  },
  welcomeTitle: {
    fontSize: '2rem',
    fontWeight: 'bold',
    margin: 0,
    color: '#1e293b'
  },
  welcomeSubtitle: {
    marginTop: '0.5rem',
    color: '#475569'
  },
  userInfoCard: {
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
    border: '1px solid #e2e8f0',
    padding: '1rem',
    marginBottom: '2rem'
  },
  userInfoTitle: {
    fontSize: '1.25rem',
    margin: '0 0 1rem',
    color: '#1e293b',
