  statsCard: {
    transition: 'transform 0.2s'
  },
  statsCardInner: {
    background: 'linear-gradient(135deg, #6b21a8, #4c1d95)',
    borderRadius: '12px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    padding: '1rem',
    color: '#fff',
    display: 'flex',
    gap: '1rem',
    alignItems: 'center'
  },
  statsCardIcon: {
    backgroundColor: 'rgba(255,255,255,0.2)',
    borderRadius: '8px',
    padding: '0.5rem',
    backdropFilter: 'blur(2px)'
  },
  statsCardLabel: {
    margin: 0,
    opacity: 0.8
  },
  statsCardValue: {
    margin: 0,
    fontSize: '1.5rem',
