  recentProjectItem: {
    marginBottom: '0.5rem'
  },
  projectMeta: {
    marginLeft: '0.5rem',
    color: '#666',
    fontSize: '0.9rem'
  },
  chartsGrid: {
    display: 'grid',
    gridTemplateColumns: '1fr 1fr', // 2 columns for the 2 charts side-by-side
    gap: '1rem',
    marginBottom: '2rem'
  },
  chartCardWide: {
    // left column
    gridColumn: '1 / 2',
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
    padding: '1rem',
    border: '1px solid #e2e8f0'
  },
  chartCard: {
    // right column
    gridColumn: '2 / 3',
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
    padding: '1rem',
    border: '1px solid #e2e8f0'
