  chartCardFull: {
    // full-width chart row below the 2-col grid
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
    padding: '1rem',
    border: '1px solid #e2e8f0',
    marginBottom: '1rem'
  },
  chartTitle: {
    fontSize: '1.25rem',
    margin: '0 0 1rem',
    color: '#1e293b',
    textAlign: 'center'
  }
