import React from 'react';
import { useQuery, gql } from '@apollo/client';
import { useAuth } from '../utils/AuthContext';

// Icons (lucide-react)
import { LogOut, Activity } from 'lucide-react';

/**
 * We include a parameter $myUserId to filter audits done by/to a specific user.
 * 
 * IMPORTANT: 
 *  - If your user ID is stored/string and not an integer, adjust the `_eq` usage 
 *    to match your schema (e.g., change Int! to String! if IDs are strings).
 *  - We then reference that parameter in the "where" clauses for 'audit'
 *    queries. Adjust field names as needed if they differ in your actual schema.
 */
const GET_ALL_MY_AUDITS = gql`
  query GetAllMyAudits($myUserId: Int!) {
    # Audits done to me
    auditsReceived: audit(where: { auditeeId: { _eq: $myUserId } }) {
      id
      grade
      createdAt
      auditorId
      comment
      object {
        id
        name
        type
      }
      auditor {
        id
        login
      }
    }

    # Audits done by me
    auditsDone: audit(where: { auditorId: { _eq: $myUserId } }) {
      id
      grade
      createdAt
      auditeeId
      comment
      object {
        id
        name
        type
      }
      auditee {
        id
        login
      }
    }

    # Aggregate Statistics (all audits)
    auditStats: audit_aggregate {
      aggregate {
        count
        avg {
          grade
        }
      }
    }
  }
`;

/**
 * MyAudits Component:
 *  - Retrieves user ID from AuthContext (so we know the "myUserId" variable)
 *  - Shows audits done by me ("auditsDone") + audits done to me ("auditsReceived")
 *  - Displays overall audit stats from "audit_aggregate"
 */
export default function MyAudits() {
  const { logout, user } = useAuth();

  // If your user object uses a string id, change $myUserId: Int! to String! in the query
  // and reference user?.id as a string. 
  const myUserId = user?.id ? parseInt(user.id, 10) : 0;

  // Pass user ID into the query
  const { loading, error, data } = useQuery(GET_ALL_MY_AUDITS, {
    variables: { myUserId },
    skip: !myUserId // Skip if userId is not available
  });

  if (!myUserId) {
    return (
      <div style={styles.centeredScreen}>
        <div style={styles.errorText}>
          No valid user ID found. Please log in.
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div style={styles.centeredScreen}>
        <div style={styles.loadingText}>Loading audits...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div style={styles.centeredScreen}>
        <div style={styles.errorText}>Error loading audits: {error.message}</div>
      </div>
    );
  }

  const { auditsReceived, auditsDone, auditStats } = data || {};
  const totalAudits = auditStats?.aggregate?.count || 0;
  const avgGrade = auditStats?.aggregate?.avg?.grade
    ? auditStats.aggregate.avg.grade.toFixed(2)
    : 'N/A';

  return (
    <div style={styles.root}>
      {/* NAVIGATION BAR */}
      <nav style={styles.navbar}>
        <div style={styles.navWrapper}>
          {/* Brand / Title */}
          <div style={styles.brandContainer}>
            <Activity style={styles.brandIcon} />
            <span style={styles.brandText}>My Audits</span>
          </div>
          {/* User Info + Logout */}
          <div style={styles.userInfoContainer}>
            <div style={{ textAlign: 'right' }}>
              <p style={{ margin: 0, fontWeight: 500 }}>{user?.login || 'Guest'}</p>
              <p style={{ margin: 0, fontSize: '0.8rem', color: '#666' }}>
                ID: {user?.id || 'N/A'}
              </p>
            </div>
            <button onClick={logout} style={styles.logoutBtn}>
              <LogOut size={18} />
              <span>Logout</span>
            </button>
          </div>
        </div>
      </nav>

      {/* MAIN CONTENT */}
      <main style={styles.main}>
        <div style={styles.container}>
          {/* STATS CARD */}
          <div style={styles.statsSummaryCard}>
            <h2 style={styles.sectionTitle}>Audit Statistics</h2>
            <div style={styles.statsSummaryContent}>
              <div>
                <strong>Total Audits:</strong> {totalAudits}
              </div>
              <div>
                <strong>Average Grade:</strong> {avgGrade}
              </div>
            </div>
          </div>

          {/* AUDITS DONE TO ME */}
          <div style={styles.auditsCard}>
            <h2 style={styles.sectionTitle}>Audits Received</h2>
            {(!auditsReceived || auditsReceived.length === 0) ? (
              <p>No audits were performed on you.</p>
            ) : (
              <ul style={styles.auditList}>
                {auditsReceived.map((audit) => (
                  <li key={audit.id} style={styles.auditItem}>
                    <div style={styles.auditMeta}>
                      <strong>Audit #{audit.id}</strong> from 
                      <span style={styles.auditorName}>
                        {' '} {audit.auditor?.login || 'Unknown Auditor'}
                      </span>
                    </div>
                    <div style={styles.auditRow}>
                      <strong>Date:</strong> {new Date(audit.createdAt).toLocaleString()}
                    </div>
                    <div style={styles.auditRow}>
                      <strong>Grade:</strong> {audit.grade}
                    </div>
                    <div style={styles.auditRow}>
                      <strong>Comment:</strong> {audit.comment || '—'}
                    </div>
                    <div style={styles.auditRow}>
                      <strong>Object:</strong> {audit.object?.name} ({audit.object?.type})
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* AUDITS DONE BY ME */}
          <div style={styles.auditsCard}>
            <h2 style={styles.sectionTitle}>Audits Done</h2>
            {(!auditsDone || auditsDone.length === 0) ? (
              <p>You haven&apos;t audited anyone yet.</p>
            ) : (
              <ul style={styles.auditList}>
                {auditsDone.map((audit) => (
                  <li key={audit.id} style={styles.auditItem}>
                    <div style={styles.auditMeta}>
                      <strong>Audit #{audit.id}</strong> for
                      <span style={styles.auditorName}>
                        {' '} {audit.auditee?.login || 'Unknown Auditee'}
                      </span>
                    </div>
                    <div style={styles.auditRow}>
                      <strong>Date:</strong> {new Date(audit.createdAt).toLocaleString()}
                    </div>
                    <div style={styles.auditRow}>
                      <strong>Grade:</strong> {audit.grade}
                    </div>
                    <div style={styles.auditRow}>
                      <strong>Comment:</strong> {audit.comment || '—'}
                    </div>
                    <div style={styles.auditRow}>
                      <strong>Object:</strong> {audit.object?.name} ({audit.object?.type})
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

