import React from 'react';
import { useQuery, gql } from '@apollo/client';
import { useAuth } from '../utils/AuthContext';

/** RECHARTS */
import {
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  AreaChart,
  Area,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar,
  BarChart,
  Bar
} from 'recharts';

/** Icons (lucide-react) */
import {
  LogOut,
  Award,
  TrendingUp,
  Clock,
  Activity
} from 'lucide-react';

/**
 * GraphQL Query uses:
 * 1) Normal queries (fields on user).
 * 2) Nested (progress -> object).
 * 3) Arguments (limit, order_by).
 */
const GET_USER_DATA = gql`
  query GetUserData {
    user {
      id
      login
      # Adjust these to match your schema if needed:
      firstName
      lastName
      email
      campus

      # XP transactions
      transactions(
        where: { type: { _eq: "xp" } }
        order_by: { createdAt: asc }
      ) {
        amount
        createdAt
        path
      }

      # Progress for pass/fail ratio
      progresses(order_by: { createdAt: asc }) {
        grade
        createdAt
        path
      }

      # Recent progress (limit 3)
      recentProgresses: progresses(order_by: { createdAt: desc }, limit: 3) {
        grade
        createdAt
        object {
          name
        }
      }
    }
  }
`;

export default function Profile() {
  const { logout } = useAuth();
  const { loading, error, data } = useQuery(GET_USER_DATA);

  if (loading) {
    return (
      <div style={styles.centeredScreen}>
        <div style={styles.loadingText}>Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div style={styles.centeredScreen}>
        <div style={styles.errorText}>Error loading data</div>
      </div>
    );
  }

  // Extract first user record
  const user = data?.user?.[0];
  if (!user) return null;

  /**
   * 1) Calculate XP Stats
   */
  const xpTransactions = user.transactions || [];
  const totalXP = xpTransactions.reduce((sum, tx) => sum + tx.amount, 0);

  // For the "XP Over Time" chart
  const xpData = xpTransactions.map((tx) => ({
    date: new Date(tx.createdAt).toLocaleDateString(),
    xp: tx.amount
  }));

  /**
   * 2) Pass/Fail Stats
   */
  const progress = user.progresses || [];
  const passCount = progress.filter((item) => item.grade > 0).length;
  const totalAudits = progress.length;
  const auditRatio = totalAudits ? ((passCount / totalAudits) * 100).toFixed(1) : 0;

  // "Projects Done" can be how many we *passed*
  const projectsDone = passCount;

  /**
   * 3) Learning Hours
   *   - Heuristic: totalXP / 100
   */
  const learningHours = Math.round(totalXP / 100);

  /**
   * 4) Recent Projects (limit 3)
   */
  const recentProjects = user.recentProgresses || [];

  /**
   * 5) RadarChart data for “Skills Overview”
   *    (example, static)
   */
  const skillsData = [
    { subject: 'Problem Solving', score: 120, fullMark: 150 },
    { subject: 'Algorithms', score: 100, fullMark: 150 },
    { subject: 'Backend Dev', score: 90, fullMark: 150 },
    { subject: 'Frontend Dev', score: 95, fullMark: 150 },
    { subject: 'DevOps', score: 80, fullMark: 150 },
    { subject: 'Database', score: 70, fullMark: 150 }
  ];

  /**
   * 6) BarChart data for “Coding Languages”
   *    (example, static)
   */
  const languageData = [
    { language: 'C', xp: 300 },
    { language: 'Go', xp: 1500 },
    { language: 'JavaScript', xp: 850 },
    { language: 'Python', xp: 700 }
  ];

  return (
    <div style={styles.root}>
      {/** NAVBAR */}
      <nav style={styles.navbar}>
        <div style={styles.navWrapper}>
          {/* Brand / Title */}
          <div style={styles.brandContainer}>
            <Activity style={styles.brandIcon} />
            <span style={styles.brandText}>Learning Analytics</span>
          </div>
          {/* User Info & Logout */}
          <div style={styles.userInfoContainer}>
            <div style={{ textAlign: 'right' }}>
              <p style={{ margin: 0, fontWeight: 500 }}>{user.login}</p>
              <p style={{ margin: 0, fontSize: '0.8rem', color: '#666' }}>
                ID: {user.id}
              </p>
            </div>
            <button onClick={logout} style={styles.logoutBtn}>
              <LogOut size={18} />
              <span>Logout</span>
            </button>
          </div>
        </div>
      </nav>

      {/** MAIN CONTENT */}
      <main style={styles.main}>
        <div style={styles.container}>
          {/* Welcome Header */}
          <div style={styles.welcomeSection}>
            <h1 style={styles.welcomeTitle}>
              Welcome back, {user.login}!
            </h1>
            <p style={styles.welcomeSubtitle}>
              Here&apos;s your learning progress overview
            </p>
          </div>

          {/** USER INFO */}
          <div style={styles.userInfoCard}>
            <h2 style={styles.userInfoTitle}>Your School Information</h2>
            <div style={styles.userInfoGrid}>
              <div style={styles.userInfoItem}>
                <strong>ID:</strong> {user.id}
              </div>
              <div style={styles.userInfoItem}>
                <strong>First Name:</strong> {user.firstName || 'N/A'}
              </div>
              <div style={styles.userInfoItem}>
                <strong>Last Name:</strong> {user.lastName || 'N/A'}
              </div>
              <div style={styles.userInfoItem}>
                <strong>Email:</strong> {user.email || 'N/A'}
              </div>
              <div style={styles.userInfoItem}>
                <strong>Campus:</strong> {user.campus || 'N/A'}
              </div>
            </div>
          </div>

          {/** STATS GRID */}
          <div style={styles.statsGrid}>
            {/* Total XP */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <TrendingUp style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Total XP</p>
                  <p style={styles.statsCardValue}>
                    {totalXP.toLocaleString()}
                  </p>
                </div>
              </div>
            </div>

            {/* Audit Ratio (Success Rate) */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <Award style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Audit Ratio</p>
                  <p style={styles.statsCardValue}>{auditRatio}%</p>
                </div>
              </div>
            </div>

            {/* Projects Done (Passed) */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <Award style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Projects Done</p>
                  <p style={styles.statsCardValue}>{projectsDone}</p>
                </div>
              </div>
            </div>

            {/* Learning Hours */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <Clock style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Learning Hours</p>
                  <p style={styles.statsCardValue}>{learningHours}</p>
                </div>
              </div>
            </div>
          </div>

          {/** RECENT PROJECTS */}
          <div style={styles.userInfoCard}>
            <h2 style={styles.userInfoTitle}>Recent Projects</h2>
            {recentProjects.length === 0 ? (
              <p>No recent projects found</p>
            ) : (
              <ul style={styles.recentProjectList}>
                {recentProjects.map((prog, idx) => (
                  <li key={idx} style={styles.recentProjectItem}>
                    <strong>{prog.object?.name || 'Unnamed Project'}</strong>
                    <span style={styles.projectMeta}>
                      (Grade: {prog.grade},{' '}
                      {new Date(prog.createdAt).toLocaleDateString()})
                    </span>
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/** CHARTS */}
          <div style={styles.chartsGrid}>
            {/* XP Over Time (AreaChart) */}
            <div style={styles.chartCardWide}>
              <h2 style={styles.chartTitle}>XP Over Time</h2>
              <div style={{ height: '300px', width: '100%' }}>
                <ResponsiveContainer width="100%" height="100%">
                  <AreaChart data={xpData}>
                    <defs>
                      <linearGradient id="colorXp" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="5%" stopColor="#6366F1" stopOpacity={0.3} />
                        <stop offset="95%" stopColor="#6366F1" stopOpacity={0} />
                      </linearGradient>
                    </defs>
                    <CartesianGrid stroke="#ccc" strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <Tooltip
                      contentStyle={{
                        backgroundColor: '#fff',
                        border: '1px solid #ccc',
                        borderRadius: '6px'
                      }}
                    />
                    <Area
                      type="monotone"
                      dataKey="xp"
                      stroke="#6366F1"
                      fill="url(#colorXp)"
                    />
                  </AreaChart>
                </ResponsiveContainer>
              </div>
            </div>

            {/* Skills Overview (RadarChart) */}
            <div style={styles.chartCard}>
              <h2 style={styles.chartTitle}>Skills Overview</h2>
              <div style={{ height: '300px', width: '100%' }}>
                <ResponsiveContainer width="100%" height="100%">
                  <RadarChart data={skillsData}>
                    <PolarGrid />
                    <PolarAngleAxis dataKey="subject" />
                    <PolarRadiusAxis />
                    <Radar
                      name="Skills"
                      dataKey="score"
                      stroke="#6366F1"
                      fill="#6366F1"
                      fillOpacity={0.3}
                    />
                  </RadarChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>

          {/* CODING LANGUAGES (BarChart) */}
          <div style={styles.chartCardFull}>
            <h2 style={styles.chartTitle}>Coding Languages</h2>
            <div style={{ height: '300px', width: '100%' }}>
              <ResponsiveContainer>
                <BarChart data={languageData}>
                  <CartesianGrid stroke="#ccc" strokeDasharray="3 3" />
                  <XAxis dataKey="language" />
                  <YAxis />
                  <Tooltip
                    contentStyle={{
                      backgroundColor: '#fff',
                      border: '1px solid #ccc',
                      borderRadius: '6px'
                    }}
                  />
                  <Bar dataKey="xp" fill="#6366F1" radius={[4, 4, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

