import React from 'react';
import { useQuery, gql } from '@apollo/client';
import { useAuth } from '../utils/AuthContext';

// Recharts
import {
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  AreaChart,
  Area,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar,
  BarChart,
  Bar
} from 'recharts';

// Icons (lucide-react)
import {
  LogOut,
  Award,
  TrendingUp,
  Clock,
  Target,
  Activity
} from 'lucide-react';

// 1) Updated GraphQL query with extra fields and nested object data for projects.
const GET_USER_DATA = gql`
  query GetUserData {
    user {
      id
      login

      # Assuming your user table has these columns:
      firstName
      lastName
      email
      campus

      # XP transactions
      transactions(where: { type: { _eq: "xp" } }, order_by: { createdAt: asc }) {
        amount
        createdAt
        path
      }

      # Recent progress (limit 3) for "recent projects"
      progresses(order_by: { createdAt: desc }, limit: 3) {
        grade
        createdAt
        # If the 'object' relationship exists, we can get the project's name:
        object {
          name
        }
      }

      # Full progress list for stats
      allProgresses: progresses(order_by: { createdAt: asc }) {
        grade
        createdAt
        path
      }
    }
  }
`;

export default function Profile() {
  const { logout } = useAuth();
  const { loading, error, data } = useQuery(GET_USER_DATA);

  if (loading) {
    return (
      <div style={styles.centeredScreen}>
        <div style={styles.loadingText}>Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div style={styles.centeredScreen}>
        <div style={styles.errorText}>Error loading data</div>
      </div>
    );
  }

  const user = data?.user[0];
  if (!user) return null;

  // Prepare data for charts
  const xpData = user.transactions.map((tx) => ({
    date: new Date(tx.createdAt).toLocaleDateString(),
    xp: tx.amount
  }));

  const totalXP = user.transactions.reduce((sum, tx) => sum + tx.amount, 0);

  // For success & audit ratio
  const passedAudits = user.allProgresses.filter((p) => p.grade > 0).length;
  const totalAudits = user.allProgresses.length;
  const successRate = totalAudits ? ((passedAudits / totalAudits) * 100).toFixed(1) : 0;
  const auditRatio = successRate; // If “audit ratio” is the same idea, you can keep them identical or rename

  // Skills data for RadarChart (example)
  const skillsData = [
    { subject: 'Problem Solving', A: 120, fullMark: 150 },
    { subject: 'Algorithms', A: 98, fullMark: 150 },
    { subject: 'Backend Dev', A: 86, fullMark: 150 },
    { subject: 'Frontend Dev', A: 99, fullMark: 150 },
    { subject: 'DevOps', A: 85, fullMark: 150 },
    { subject: 'Database', A: 65, fullMark: 150 }
  ];

  // Example data for BarChart
  const projectData = [
    { name: 'Go', completed: 15, total: 20 },
    { name: 'JavaScript', completed: 12, total: 15 },
    { name: 'Python', completed: 8, total: 10 },
    { name: 'Docker', completed: 5, total: 8 }
  ];

  // Recent projects from "progresses(limit: 3)"
  const recentProjects = user.progresses.map((prog) => ({
    name: prog?.object?.name || 'Unnamed Project',
    createdAt: new Date(prog.createdAt).toLocaleDateString(),
    grade: prog.grade
  }));

  return (
    <div style={styles.root}>
      {/** NAVIGATION BAR */}
      <nav style={styles.navbar}>
        <div style={styles.navWrapper}>
          {/* Brand / Title */}
          <div style={styles.brandContainer}>
            <Activity style={styles.brandIcon} />
            <span style={styles.brandText}>
              Learning Analytics
            </span>
          </div>
          {/* User Info + Logout */}
          <div style={styles.userInfoContainer}>
            <div style={{ textAlign: 'right' }}>
              <p style={{ margin: 0, fontWeight: 500 }}>{user.login}</p>
              <p style={{ margin: 0, fontSize: '0.8rem', color: '#666' }}>
                ID: {user.id}
              </p>
            </div>
            <button
              onClick={logout}
              style={styles.logoutBtn}
            >
              <LogOut size={18} />
              <span>Logout</span>
            </button>
          </div>
        </div>
      </nav>

      {/** MAIN CONTENT */}
      <main style={styles.main}>
        <div style={styles.container}>
          {/* Welcome header */}
          <div style={styles.welcomeSection}>
            <h1 style={styles.welcomeTitle}>
              Welcome back, {user.login}!
            </h1>
            <p style={styles.welcomeSubtitle}>
              Here&apos;s your learning progress overview
            </p>
          </div>

          {/** USER INFO CARD */}
          <div style={styles.userInfoCard}>
            <h2 style={styles.userInfoTitle}>User Info</h2>
            <div style={styles.userInfoGrid}>
              <div style={styles.userInfoItem}>
                <strong>ID:</strong> {user.id}
              </div>
              <div style={styles.userInfoItem}>
                <strong>First Name:</strong> {user.firstName || 'N/A'}
              </div>
              <div style={styles.userInfoItem}>
                <strong>Last Name:</strong> {user.lastName || 'N/A'}
              </div>
              <div style={styles.userInfoItem}>
                <strong>Email:</strong> {user.email || 'N/A'}
              </div>
              <div style={styles.userInfoItem}>
                <strong>Campus:</strong> {user.campus || 'N/A'}
              </div>
            </div>
          </div>

          {/** STATS GRID */}
          <div style={styles.statsGrid}>
            {/* Total XP */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <TrendingUp style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Total XP</p>
                  <p style={styles.statsCardValue}>{totalXP.toLocaleString()}</p>
                </div>
              </div>
            </div>

            {/* Success Rate */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <Award style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Success Rate</p>
                  <div style={{ display: 'flex', alignItems: 'baseline', gap: '0.25rem' }}>
                    <p style={styles.statsCardValue}>{successRate}%</p>
                    <p style={{ fontSize: '0.9rem', opacity: 0.8 }}>
                      ({passedAudits}/{totalAudits})
                    </p>
                  </div>
                </div>
              </div>
            </div>

            {/* Projects Done */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <Target style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Projects Done</p>
                  <p style={styles.statsCardValue}>{totalAudits}</p>
                </div>
              </div>
            </div>

            {/* Learning Hours */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <Clock style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Learning Hours</p>
                  <p style={styles.statsCardValue}>{Math.round(totalXP / 1000)}</p>
                </div>
              </div>
            </div>

            {/* Audit Ratio */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <Award style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Audit Ratio</p>
                  <p style={styles.statsCardValue}>{auditRatio}%</p>
                </div>
              </div>
            </div>
          </div>

          {/** RECENT PROJECTS */}
          <div style={styles.recentProjectsCard}>
            <h2 style={styles.chartTitle}>Recent Projects</h2>
            {recentProjects.length === 0 ? (
              <p>No recent projects found</p>
            ) : (
              <ul style={styles.recentProjectList}>
                {recentProjects.map((proj, i) => (
                  <li key={i} style={styles.recentProjectItem}>
                    <strong>{proj.name}</strong>  
                    <span style={styles.projectMeta}>
                      (Grade: {proj.grade}, {proj.createdAt})
                    </span>
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/** CHARTS GRID */}
          <div style={styles.chartsGrid}>
            {/* XP Progress (AreaChart) */}
            <div style={styles.chartCardWide}>
              <h2 style={styles.chartTitle}>XP Progress</h2>
              <div style={{ height: '300px', width: '100%' }}>
                <ResponsiveContainer width="100%" height="100%">
                  <AreaChart data={xpData}>
                    <defs>
                      <linearGradient id="colorXp" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="5%" stopColor="#6366F1" stopOpacity={0.3} />
                        <stop offset="95%" stopColor="#6366F1" stopOpacity={0} />
                      </linearGradient>
                    </defs>
                    <CartesianGrid stroke="#ccc" strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <Tooltip
                      contentStyle={{
                        backgroundColor: '#fff',
                        border: '1px solid #ccc',
                        borderRadius: '6px'
                      }}
                    />
                    <Area
                      type="monotone"
                      dataKey="xp"
                      stroke="#6366F1"
                      fill="url(#colorXp)"
                    />
                  </AreaChart>
                </ResponsiveContainer>
              </div>
            </div>

            {/* Skills Overview (RadarChart) */}
            <div style={styles.chartCard}>
              <h2 style={styles.chartTitle}>Skills Overview</h2>
              <div style={{ height: '300px', width: '100%' }}>
                <ResponsiveContainer width="100%" height="100%">
                  <RadarChart data={skillsData}>
                    <PolarGrid />
                    <PolarAngleAxis dataKey="subject" />
                    <PolarRadiusAxis />
                    <Radar
                      name="Skills"
                      dataKey="A"
                      stroke="#6366F1"
                      fill="#6366F1"
                      fillOpacity={0.3}
                    />
                  </RadarChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>

          {/** PROJECT PROGRESS (BarChart) */}
          <div style={styles.chartCard}>
            <h2 style={styles.chartTitle}>Project Progress</h2>
            <div style={{ height: '300px', width: '100%' }}>
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={projectData}>
                  <CartesianGrid stroke="#ccc" strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip
                    contentStyle={{
                      backgroundColor: '#fff',
                      border: '1px solid #ccc',
                      borderRadius: '6px'
                    }}
                  />
                  <Bar dataKey="completed" fill="#6366F1" radius={[4, 4, 0, 0]} />
                  <Bar dataKey="total" fill="#CBD5E1" radius={[4, 4, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

/** Inline Styles for Profile page **/

const styles = {
  root: {
    minHeight: '100vh',
    backgroundColor: '#f8fafc',
    fontFamily: '"Segoe UI", Tahoma, Geneva, Verdana, sans-serif',
    color: '#111'
  },
  centeredScreen: {
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#f8fafc'
  },
  loadingText: {
    fontSize: '24px',
    color: '#555',
    opacity: 0.8,
    animation: 'pulse 2s infinite'
