import React from 'react';
import { useQuery, gql } from '@apollo/client';
import { useAuth } from '../utils/AuthContext';

// Recharts
import {
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  AreaChart,
  Area,
  PieChart,
  Pie,
  Cell,
  LineChart,
  Line
} from 'recharts';

// Icons (lucide-react)
import {
  LogOut,
  Award,
  TrendingUp,
  Clock,
  Activity
} from 'lucide-react';

/**
 * UPDATED GraphQL QUERY:
 *  - Instead of "progresses," we fetch "audits."
 *  - Adjust the fields (e.g., `comment`, `auditor`, `auditee`) to match your actual audit schema.
 *  - If your "audits" relationship name differs in Hasura (e.g. "audit_records"), update accordingly.
 */
const GET_USER_DATA = gql`
  query GetUserData {
    user {
      id
      login
      firstName
      lastName
      email
      campus

      # Transactions: Filter to XP type, ordered by ascending creation date
      transactions(where: { type: { _eq: "xp" } }, order_by: { createdAt: asc }) {
        amount
        createdAt
        path
      }

      # All audits
      audits(order_by: { createdAt: asc }) {
        id
        grade
        createdAt
        comment
        auditorId
        auditeeId
        auditor {
          id
          login
        }
        auditee {
          id
          login
        }
      }
    }
  }
`;

/**
 * Profile Component:
 *  Displays user info, XP stats, audit stats, plus charts using Recharts.
 *  The "audits" array replaces "progresses" from your old code.
 */
export default function Profile() {
  const { logout } = useAuth();
  const { loading, error, data } = useQuery(GET_USER_DATA);

  if (loading) {
    return (
      <div style={styles.centeredScreen}>
        <div style={styles.loadingText}>Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div style={styles.centeredScreen}>
        <div style={styles.errorText}>Error loading data</div>
      </div>
    );
  }

  // Safely extract the first user
  const user = data?.user?.[0];
  if (!user) return null;

  //-----------------------
  //   DATA PROCESSING
  //-----------------------

  // 1) XP DATA (AreaChart)
  const xpData = (user.transactions || []).map((tx) => ({
    date: new Date(tx.createdAt).toLocaleDateString(),
    xp: tx.amount,
  }));
  const totalXP = xpData.reduce((sum, item) => sum + item.xp, 0);

  // 2) AUDIT STATS
  //    Assuming "grade > 0" means a passing audit. Adjust as needed.
  const allAudits = user.audits || [];
  const passedAudits = allAudits.filter((a) => a.grade > 0).length;
  const totalAudits = allAudits.length;
  const auditRatio = totalAudits
    ? ((passedAudits / totalAudits) * 100).toFixed(1)
    : 0;

  // 3) LEARNING HOURS (based on XP)
  //    If you no longer want this, you can remove this section + the card below.
  const learningHours = Math.round(totalXP / 1000);

  // 4) RECENT AUDITS (limit 5) - Replacing "recentProjects" logic
  //    Sort by descending date, then slice the last 5.
  const sortedAuditsDesc = [...allAudits].sort(
    (a, b) => new Date(b.createdAt).valueOf() - new Date(a.createdAt).valueOf()
  );
  const recentAudits = sortedAuditsDesc.slice(0, 5).map((audit) => ({
    id: audit.id,
    grade: audit.grade,
    createdAt: new Date(audit.createdAt).toLocaleDateString(),
    auditor: audit.auditor?.login || 'Unknown',
    auditee: audit.auditee?.login || 'Unknown',
    comment: audit.comment || '—'
  }));

  // 5) GRADE OVER TIME (LineChart)
  //    Ascending by creation date for the line chart
  const gradeOverTimeData = allAudits.map((a) => ({
    date: new Date(a.createdAt).toLocaleDateString(),
    grade: a.grade,
  }));

  // 6) XP by Path (PieChart)
  const xpByPathMap = {};
  (user.transactions || []).forEach((tx) => {
    const segments = tx.path ? tx.path.split('/') : [];
    const mainSegment = segments[2] || 'unknown';
    if (!xpByPathMap[mainSegment]) {
      xpByPathMap[mainSegment] = 0;
    }
    xpByPathMap[mainSegment] += tx.amount;
  });
  const xpByPathData = Object.entries(xpByPathMap).map(([name, value]) => ({
    name,
    value,
  }));
  const COLORS = ['#6366F1', '#DB2777', '#F97316', '#10B981', '#1D4ED8', '#9333EA'];

  //-----------------------
  //     JSX RENDERING
  //-----------------------

  return (
    <div style={styles.root}>
      {/* NAVIGATION BAR */}
      <nav style={styles.navbar}>
        <div style={styles.navWrapper}>
          <div style={styles.brandContainer}>
            <Activity style={styles.brandIcon} />
            <span style={styles.brandText}>Learning Analytics</span>
          </div>
          <div style={styles.userInfoContainer}>
            <div style={{ textAlign: 'right' }}>
              <p style={{ margin: 0, fontWeight: 500 }}>{user.login}</p>
              <p style={{ margin: 0, fontSize: '0.8rem', color: '#666' }}>
                ID: {user.id}
              </p>
            </div>
            <button onClick={logout} style={styles.logoutBtn}>
              <LogOut size={18} />
              <span>Logout</span>
            </button>
          </div>
        </div>
      </nav>

      {/* MAIN CONTENT */}
      <main style={styles.main}>
        <div style={styles.container}>
          {/* Welcome header */}
          <div style={styles.welcomeSection}>
            <h1 style={styles.welcomeTitle}>Welcome back, {user.login}!</h1>
            <p style={styles.welcomeSubtitle}>
              Here&apos;s your learning progress overview
            </p>
          </div>

          {/* USER INFO CARD */}
          <div style={styles.userInfoCard}>
            <h2 style={styles.userInfoTitle}>User Info</h2>
            <div style={styles.userInfoGrid}>
              <div style={styles.userInfoItem}>
                <strong>ID:</strong> {user.id}
              </div>
              <div style={styles.userInfoItem}>
                <strong>First Name:</strong> {user.firstName || 'N/A'}
              </div>
              <div style={styles.userInfoItem}>
                <strong>Last Name:</strong> {user.lastName || 'N/A'}
              </div>
              <div style={styles.userInfoItem}>
                <strong>Email:</strong> {user.email || 'N/A'}
              </div>
              <div style={styles.userInfoItem}>
                <strong>Campus:</strong> {user.campus || 'N/A'}
              </div>
            </div>
          </div>

          {/* STATS GRID */}
          <div style={styles.statsGrid}>
            {/* Total XP */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <TrendingUp style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Total XP</p>
                  <p style={styles.statsCardValue}>{totalXP.toLocaleString()}</p>
                </div>
              </div>
            </div>

            {/* Learning Hours */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <Clock style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Learning Hours</p>
                  <p style={styles.statsCardValue}>{learningHours}</p>
                </div>
              </div>
            </div>

            {/* Audit Ratio */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <Award style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Audit Stats</p>
                  <div
                    style={{
                      display: 'flex',
                      flexDirection: 'column',
                      fontSize: '0.9rem',
                      marginTop: '0.25rem'
                    }}
                  >
                    <span><strong>Pass:</strong> {passedAudits}</span>
                    <span><strong>Total:</strong> {totalAudits}</span>
                    <span><strong>Ratio:</strong> {auditRatio}%</span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* RECENT AUDITS (limit 5) */}
          <div style={styles.recentProjectsCard}>
            <h2 style={styles.chartTitle}>Recent Audits</h2>
            {recentAudits.length === 0 ? (
              <p>No recent audits found</p>
            ) : (
              <ul style={styles.recentProjectList}>
                {recentAudits.map((audit) => (
                  <li key={audit.id} style={styles.recentProjectItem}>
                    <strong>Audit #{audit.id}</strong>
                    <span style={styles.projectMeta}>
                      (Grade: {audit.grade}, {audit.createdAt}, 
                      Auditor: {audit.auditor}, Auditee: {audit.auditee})
                    </span>
                    <p style={styles.projectMeta}>
                      Comment: {audit.comment}
                    </p>
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* CHARTS GRID */}
          <div style={styles.chartsGrid}>
            {/* XP Progress (AreaChart) */}
            <div style={styles.chartCardWide}>
              <h2 style={styles.chartTitle}>XP Progress</h2>
              <div style={{ height: '300px', width: '100%' }}>
                <ResponsiveContainer width="100%" height="100%">
                  <AreaChart data={xpData}>
                    <defs>
                      <linearGradient id="colorXp" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="5%" stopColor="#6366F1" stopOpacity={0.3} />
                        <stop offset="95%" stopColor="#6366F1" stopOpacity={0} />
                      </linearGradient>
                    </defs>
                    <CartesianGrid stroke="#ccc" strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <Tooltip
                      contentStyle={{
                        backgroundColor: '#fff',
                        border: '1px solid #ccc',
                        borderRadius: '6px'
                      }}
                    />
                    <Area
                      type="monotone"
                      dataKey="xp"
                      stroke="#6366F1"
                      fill="url(#colorXp)"
                    />
                  </AreaChart>
                </ResponsiveContainer>
              </div>
            </div>

            {/* Grade Over Time (LineChart) */}
            <div style={styles.chartCard}>
              <h2 style={styles.chartTitle}>Grade Over Time</h2>
              <div style={{ height: '300px', width: '100%' }}>
                {gradeOverTimeData.length === 0 ? (
                  <p style={{ textAlign: 'center' }}>No grade data found</p>
                ) : (
                  <ResponsiveContainer width="100%" height="100%">
                    <LineChart data={gradeOverTimeData}>
                      <CartesianGrid stroke="#ccc" strokeDasharray="3 3" />
                      <XAxis dataKey="date" />
                      <YAxis />
                      <Tooltip
                        contentStyle={{
                          backgroundColor: '#fff',
                          border: '1px solid #ccc',
                          borderRadius: '6px'
                        }}
                      />
                      <Line
                        type="monotone"
                        dataKey="grade"
                        stroke="#F97316"
                        strokeWidth={3}
                        dot={{ r: 4 }}
                        activeDot={{ r: 6 }}
                      />
                    </LineChart>
                  </ResponsiveContainer>
                )}
              </div>
            </div>
          </div>

          {/* XP by Path (PieChart) */}
          <div style={styles.chartCard}>
            <h2 style={styles.chartTitle}>XP by Path</h2>
            <div style={{ height: '300px', width: '100%' }}>
              {xpByPathData.length === 0 ? (
                <p style={{ textAlign: 'center' }}>No XP path data found</p>
              ) : (
                <ResponsiveContainer width="100%" height="100%">
                  <PieChart>
                    <Pie
                      data={xpByPathData}
                      dataKey="value"
                      nameKey="name"
                      outerRadius={100}
                      fill="#6366F1"
                      label={({ name, value }) => `${name} (${value})`}
                    >
                      {xpByPathData.map((_entry, i) => (
                        <Cell key={`cell-${i}`} fill={COLORS[i % COLORS.length]} />
                      ))}
                    </Pie>
                    <Tooltip
                      contentStyle={{
                        backgroundColor: '#fff',
                        border: '1px solid #ccc',
                        borderRadius: '6px'
                      }}
                    />
                  </PieChart>
                </ResponsiveContainer>
              )}
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

// Inline styles remain unchanged
const styles = {
  root: {
