import React from 'react';
import { useQuery, gql } from '@apollo/client';
import { useAuth } from '../utils/AuthContext';

// Icons (lucide-react)
import {
  LogOut,
  Award,
  TrendingUp,
  Clock,
  Activity
} from 'lucide-react';

/**
 * 1) UPDATED GraphQL query
 *    Includes transactions (XP), progresses, and results with nested object
 */
const GET_USER_DATA = gql`
  query GetUserData {
    user {
      id
      login
      transactions(where: {type: {_eq: "xp"}}, order_by: {createdAt: asc}) {
        amount
        createdAt
        path
        object {
          id
          name
          type
        }
      }
      progresses(order_by: {createdAt: asc}) {
        grade
        createdAt
        path
        object {
          id
          name
          type
        }
      }
      results(order_by: {createdAt: asc}) {
        grade
        createdAt
        path
        object {
          id
          name
          type
        }
      }
    }
  }
`;

/**
 * 2) Data processing helpers (from your instructions)
 */

// XP data processing
const processXPData = (transactions) => {
  return transactions.reduce((acc, tx) => {
    const date = new Date(tx.createdAt).toLocaleDateString();
    acc[date] = (acc[date] || 0) + tx.amount;
    return acc;
  }, {});
};

// Project data processing
const processProjectData = (progresses) => {
  return progresses.reduce((acc, prog) => {
    const projectName = prog.object?.name || prog.path;
    if (!acc[projectName]) {
      acc[projectName] = { passed: 0, failed: 0, total: 0 };
    }
    acc[projectName].total++;
    if (prog.grade > 0) acc[projectName].passed++;
    else acc[projectName].failed++;
    return acc;
  }, {});
};

// Audit data processing
const processAuditData = (results) => {
  return results.reduce((acc, res) => {
    // We'll bucket them by "month" for the bar chart
    const month = new Date(res.createdAt).toLocaleDateString('en-US', {
      month: 'short'
    });
    if (!acc[month]) acc[month] = { passed: 0, failed: 0 };
    if (res.grade > 0) acc[month].passed++;
    else acc[month].failed++;
    return acc;
  }, {});
};

/**
 * 3) SVG helper functions
 */
// createPathD: builds an SVG Path's "d" attribute
const createPathD = (points, width, height) => {
  return points
    .map((point, i) => `${i === 0 ? 'M' : 'L'} ${point.x * width} ${point.y * height}`)
    .join(' ');
};

// calculatePoints: normalizes data to (x, y) points in [0..1] coords
const calculatePoints = (data, width, height) => {
  const max = Math.max(...Object.values(data));
  const entries = Object.entries(data);
  if (entries.length <= 1) {
    // Avoid dividing by zero if there's only 1 point
    return entries.map(([_, value], i) => ({
      x: 0.5, 
      y: 1 - value / max
    }));
  }
  return entries.map(([_, value], i) => ({
    x: i / (entries.length - 1),
    y: 1 - value / max
  }));
};

/**
 * 4) Custom SVG Components
 */

// XPLineGraph: line chart of XP over time
function XPLineGraph({ data, width = 600, height = 300 }) {
  const points = calculatePoints(data, width, height);
  const d = createPathD(points, width, height);

  // We'll also mark small circles at each data point
  return (
    <svg width="100%" height="100%" viewBox={`0 0 ${width} ${height}`} style={{ background: '#f9fafb' }}>
      <polyline 
        fill="none" 
        stroke="#e5e7eb" 
        strokeWidth="1" 
        points={`0,${height} ${width},${height}`}
      />
      <path d={d} stroke="#6366F1" strokeWidth="2" fill="none" />
      {points.map((pt, idx) => (
        <circle
          key={idx}
          cx={pt.x * width}
          cy={pt.y * height}
          r="4"
          fill="#6366F1"
        />
      ))}
    </svg>
  );
}

// ProjectPieChart: pie chart showing pass/fail ratio for all projects combined
// or some aggregated metric
function ProjectPieChart({ data, size = 300 }) {
  // We'll sum all passed/failed across all projects
  let totalPassed = 0;
  let totalFailed = 0;
  Object.values(data).forEach(({ passed, failed }) => {
    totalPassed += passed;
    totalFailed += failed;
  });
  const total = totalPassed + totalFailed || 1; // avoid div by zero

  // "passedAngle" fraction from 0..2π
  const passedAngle = (totalPassed / total) * 2 * Math.PI;

  // We'll draw two arcs: pass + fail
  const center = size / 2;
  const radius = size / 2 - 10; // some padding

  // Helper to convert angle -> x,y
  const polarToCartesian = (cx, cy, r, angle) => [
    cx + r * Math.cos(angle),
    cy + r * Math.sin(angle)
  ];

  // Build arc path
  const buildArc = (startAngle, endAngle, color) => {
    const largeArc = endAngle - startAngle > Math.PI ? 1 : 0;
    const [startX, startY] = polarToCartesian(center, center, radius, startAngle);
    const [endX, endY] = polarToCartesian(center, center, radius, endAngle);
    return (
      <path
        d={[
          `M ${startX} ${startY}`,
          `A ${radius} ${radius} 0 ${largeArc} 1 ${endX} ${endY}`,
          `L ${center} ${center}`,
          'Z'
        ].join(' ')}
        fill={color}
      />
    );
  };

  return (
    <svg width={size} height={size}>
      {/* Pass arc */}
      {buildArc(0, passedAngle, '#10B981')} 
      {/* Fail arc */}
      {buildArc(passedAngle, 2 * Math.PI, '#EF4444')}
      <circle cx={center} cy={center} r={radius * 0.5} fill="#fff" opacity={0.1} />
      {/* Legend-ish text in center */}
      <text
        x={center}
        y={center}
        textAnchor="middle"
        fill="#111"
        fontSize="1rem"
        fontWeight="bold"
        dy=".3em"
      >
        {`Pass: ${totalPassed}, Fail: ${totalFailed}`}
      </text>
    </svg>
  );
}

// AuditBarGraph: bar chart by month for passed/failed
function AuditBarGraph({ data, width = 600, height = 300 }) {
  const months = Object.keys(data);
  if (!months.length) {
    return <p>No audit data found</p>;
  }

  // We'll figure out the max possible "passed + failed" to scale bars
  let maxCount = 0;
  months.forEach((month) => {
    const { passed, failed } = data[month];
    const total = passed + failed;
    if (total > maxCount) {
      maxCount = total;
    }
  });
  maxCount = maxCount || 1;

  const barWidth = width / (months.length * 2);

  return (
    <svg
      width="100%"
      height="100%"
      viewBox={`0 0 ${width} ${height}`}
      style={{ background: '#f9fafb' }}
    >
      {/* X-axis line */}
      <line
        x1="0"
        y1={height - 1}
        x2={width}
        y2={height - 1}
        stroke="#ddd"
        strokeWidth="2"
      />
      {months.map((month, idx) => {
        const { passed, failed } = data[month];
        const total = passed + failed;
        // scale
        const barHeight = (total / maxCount) * (height - 20); // some top padding
        const x = idx * (barWidth * 2) + barWidth;
        const y = height - barHeight - 1;

        return (
          <g key={month}>
            {/* Outer rect for total attempts */}
            <rect
              x={x - (barWidth / 2)}
              y={y}
              width={barWidth}
              height={barHeight}
              fill="#EF4444"
              opacity={0.5}
            />
            {/* Inner portion for "passed" ratio */}
            {passed > 0 && (
              <rect
                x={x - (barWidth / 2)}
                y={y + barHeight * (1 - passed / total)}
                width={barWidth}
                height={barHeight * (passed / total)}
                fill="#10B981"
              />
            )}
            {/* Label for month below the bar */}
            <text
              x={x}
              y={height - 3}
              textAnchor="middle"
              fill="#111"
              fontSize="0.8rem"
            >
              {month}
            </text>
          </g>
        );
      })}
    </svg>
  );
}

/**
 * Main Profile Component
 */
export default function Profile() {
  const { logout } = useAuth();
  const { loading, error, data } = useQuery(GET_USER_DATA);

  if (loading) {
    return (
      <div style={styles.centeredScreen}>
        <div style={styles.loadingText}>Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div style={styles.centeredScreen}>
        <div style={styles.errorText}>Error loading data</div>
      </div>
    );
  }

  // There's only one user row for your authenticated user
  const user = data?.user?.[0];
  if (!user) return null;

  // 1) XP Data
  const xpObj = processXPData(user.transactions || []);

  // Turn xpObj => array or keep object for line chart
  // We'll pass it directly to XPLineGraph as an object

  // 2) Projects data (pass/fail ratio per project)
  const projectObj = processProjectData(user.progresses || []);

  // 3) Audit data by month from results
  const auditObj = processAuditData(user.results || []);

  // Also get some overall stats
  const totalXP = Object.values(xpObj).reduce((acc, val) => acc + val, 0);
  const totalPassed = Object.values(projectObj).reduce((acc, proj) => acc + proj.passed, 0);
  const totalFailed = Object.values(projectObj).reduce((acc, proj) => acc + proj.failed, 0);
  const totalProjects = totalPassed + totalFailed;
  const auditRatio = totalProjects ? ((totalPassed / totalProjects) * 100).toFixed(1) : 0;
  const learningHours = Math.round(totalXP / 100);

  return (
    <div style={styles.root}>
      {/* NAVBAR */}
      <nav style={styles.navbar}>
        <div style={styles.navWrapper}>
          {/* Brand / Title */}
          <div style={styles.brandContainer}>
            <Activity style={styles.brandIcon} />
            <span style={styles.brandText}>Learning Analytics</span>
          </div>
          {/* User Info & Logout */}
          <div style={styles.userInfoContainer}>
            <div style={{ textAlign: 'right' }}>
              <p style={{ margin: 0, fontWeight: 500 }}>{user.login}</p>
              <p style={{ margin: 0, fontSize: '0.8rem', color: '#666' }}>
                ID: {user.id}
              </p>
            </div>
            <button onClick={logout} style={styles.logoutBtn}>
              <LogOut size={18} />
              <span>Logout</span>
            </button>
          </div>
        </div>
      </nav>

      {/* MAIN CONTENT */}
      <main style={styles.main}>
        <div style={styles.container}>
          {/* Welcome Header */}
          <div style={styles.welcomeSection}>
            <h1 style={styles.welcomeTitle}>
              Welcome back, {user.login}!
            </h1>
            <p style={styles.welcomeSubtitle}>
              Here&apos;s your learning progress overview
            </p>
          </div>

          {/* USER INFO (Minimal) */}
          <div style={styles.userInfoCard}>
            <h2 style={styles.userInfoTitle}>User Information</h2>
            <div style={styles.userInfoGrid}>
              <div style={styles.userInfoItem}>
                <strong>ID:</strong> {user.id}
              </div>
              <div style={styles.userInfoItem}>
                <strong>Login:</strong> {user.login}
              </div>
            </div>
          </div>

          {/* STATS GRID */}
          <div style={styles.statsGrid}>
            {/* Total XP */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <TrendingUp style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Total XP</p>
                  <p style={styles.statsCardValue}>
                    {totalXP.toLocaleString()}
                  </p>
                </div>
              </div>
            </div>

            {/* Audit Ratio (aka pass ratio) */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <Award style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Audit Ratio</p>
                  <p style={styles.statsCardValue}>{auditRatio}%</p>
                </div>
              </div>
            </div>

            {/* Total Projects Done */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <Award style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Projects Done</p>
                  <p style={styles.statsCardValue}>{totalProjects}</p>
                </div>
              </div>
            </div>

            {/* Learning Hours */}
            <div style={styles.statsCard}>
              <div style={styles.statsCardInner}>
                <div style={styles.statsCardIcon}>
                  <Clock style={{ color: '#fff' }} />
                </div>
                <div>
                  <p style={styles.statsCardLabel}>Learning Hours</p>
                  <p style={styles.statsCardValue}>{learningHours}</p>
                </div>
              </div>
            </div>
          </div>

          {/* CHARTS */}
          <div style={styles.chartsGrid}>
            {/* 1) XPLineGraph (XP Over Time) */}
            <div style={styles.chartCardWide}>
              <h2 style={styles.chartTitle}>XP Over Time</h2>
              <div style={{ width: '100%', height: '300px' }}>
                {Object.keys(xpObj).length === 0 ? (
                  <p style={{ textAlign: 'center' }}>No XP data found</p>
                ) : (
                  <XPLineGraph data={xpObj} width={600} height={300} />
                )}
              </div>
            </div>

            {/* 2) ProjectPieChart (Pass/Fail ratio by project) */}
            <div style={styles.chartCard}>
              <h2 style={styles.chartTitle}>Project Pass/Fail (Pie)</h2>
              <div style={{ width: '100%', height: '300px', display: 'flex', justifyContent: 'center' }}>
                {Object.keys(projectObj).length === 0 ? (
                  <p style={{ textAlign: 'center' }}>No project data found</p>
                ) : (
                  <ProjectPieChart data={projectObj} size={250} />
                )}
              </div>
            </div>
          </div>

          {/* 3) AuditBarGraph (Monthly pass/fail from "results") */}
          <div style={styles.chartCardFull}>
            <h2 style={styles.chartTitle}>Monthly Audits</h2>
            <div style={{ width: '100%', height: '300px' }}>
              {Object.keys(auditObj).length === 0 ? (
                <p style={{ textAlign: 'center' }}>No audit data found</p>
              ) : (
                <AuditBarGraph data={auditObj} width={600} height={300} />
              )}
            </div>
          </div>

        </div>
      </main>
    </div>
  );
}

