import React from 'react';
import { useQuery, gql } from '@apollo/client';
import { useAuth } from '../utils/AuthContext';

// Recharts
import {
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  AreaChart,
  Area,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar,
  BarChart,
  Bar,
  PieChart,
  Pie,
  Cell,
  LineChart,
  Line
} from 'recharts';

// Icons (lucide-react)
import {
  LogOut,
  Award,
  TrendingUp,
  Clock,
  Target,
  Activity
} from 'lucide-react';

// 1) Updated GraphQL query with extra fields and nested object data for projects.
const GET_USER_DATA = gql`
  query GetUserData {
    user {
      id
      login

      # Assuming your user table has these columns:
      firstName
      lastName
      email
      campus

      # XP transactions
      transactions(where: { type: { _eq: "xp" } }, order_by: { createdAt: asc }) {
        amount
        createdAt
        path
      }

      # Recent progress (limit 3) for "recent projects"
      progresses(order_by: { createdAt: desc }, limit: 3) {
        grade
        createdAt
        # If the 'object' relationship exists, we can get the project's name:
        object {
          name
        }
      }

      # Full progress list for stats
      allProgresses: progresses(order_by: { createdAt: asc }) {
        grade
        createdAt
        path
      }
    }
  }
`;

export default function Profile() {
  const { logout } = useAuth();
  const { loading, error, data } = useQuery(GET_USER_DATA);

  if (loading) {
    return (
      <div style={styles.centeredScreen}>
        <div style={styles.loadingText}>Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div style={styles.centeredScreen}>
        <div style={styles.errorText}>Error loading data</div>
      </div>
    );
  }

  // Safely extract the first user row
  const user = data?.user?.[0];
  if (!user) return null;

  // 2) Prepare data for charts
  // 2a) XP data for AreaChart
  const xpData = (user.transactions || []).map((tx) => ({
    date: new Date(tx.createdAt).toLocaleDateString(),
    xp: tx.amount
  }));
  const totalXP = xpData.reduce((sum, item) => sum + item.xp, 0);

  // 2b) Full progress for pass/fail stats
  const passedAudits = (user.allProgresses || []).filter((p) => p.grade > 0).length;
  const totalAudits = (user.allProgresses || []).length;
  const successRate = totalAudits ? ((passedAudits / totalAudits) * 100).toFixed(1) : 0;
  const auditRatio = successRate; // or rename if needed
  const learningHours = Math.round(totalXP / 1000);

  // 2c) RadarChart data (example placeholders)
  const skillsData = [
    { subject: 'Problem Solving', A: 120, fullMark: 150 },
    { subject: 'Algorithms', A: 98, fullMark: 150 },
    { subject: 'Backend Dev', A: 86, fullMark: 150 },
    { subject: 'Frontend Dev', A: 99, fullMark: 150 },
    { subject: 'DevOps', A: 85, fullMark: 150 },
    { subject: 'Database', A: 65, fullMark: 150 }
  ];

  // 2d) Example data for BarChart
  const projectData = [
    { name: 'Go', completed: 15, total: 20 },
    { name: 'JavaScript', completed: 12, total: 15 },
    { name: 'Python', completed: 8, total: 10 },
    { name: 'Docker', completed: 5, total: 8 }
  ];

  // 2e) Recent projects
  const recentProjects = (user.progresses || []).map((prog) => ({
    name: prog?.object?.name || 'Unnamed Project',
    createdAt: new Date(prog.createdAt).toLocaleDateString(),
    grade: prog.grade
  }));

  // 2f) Extra charts below, as requested:

  /**
   * Grade Over Time (LineChart)
   * We'll build an array from user.allProgresses: { date, grade }
   */
  const gradeOverTimeData = (user.allProgresses || []).map((p) => ({
    date: new Date(p.createdAt).toLocaleDateString(),
    grade: p.grade
  }));

  /**
   * XP by Path (PieChart)
   * We'll sum xp by top-level path segment (e.g. parse "path"?).
   */
  const xpByPathMap = {}; // { pathSegment: totalXP }
  (user.transactions || []).forEach((tx) => {
    // Example path: "/madere/piscine-go/quest-01"
    // split => ["", "madere", "piscine-go", "quest-01", ...]
    const segments = tx.path.split('/');
    const mainSegment = segments[2] || 'unknown';
    if (!xpByPathMap[mainSegment]) {
      xpByPathMap[mainSegment] = 0;
    }
    xpByPathMap[mainSegment] += tx.amount;
  });
  const xpByPathData = Object.entries(xpByPathMap).map(([name, value]) => ({
    name,
    value
  }));
  const COLORS = ['#6366F1', '#DB2777', '#F97316', '#10B981', '#1D4ED8', '#9333EA'];

  return (
    <div style={styles.root}>
