          {/** PROJECT PROGRESS (BarChart) */}
          <div style={styles.chartCard}>
            <h2 style={styles.chartTitle}>Project Progress</h2>
            <div style={{ height: '300px', width: '100%' }}>
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={projectData}>
                  <CartesianGrid stroke="#ccc" strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip
                    contentStyle={{
                      backgroundColor: '#fff',
                      border: '1px solid #ccc',
                      borderRadius: '6px'
                    }}
                  />
                  {/* completed in one bar, total in another */}
                  <Bar dataKey="completed" fill="#6366F1" radius={[4, 4, 0, 0]} />
                  <Bar dataKey="total" fill="#CBD5E1" radius={[4, 4, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/** ADDITIONAL CHARTS BELOW */}

          {/* Grade Over Time (LineChart) */}
          <div style={styles.chartCard}>
            <h2 style={styles.chartTitle}>Grade Over Time</h2>
            <div style={{ height: '300px', width: '100%' }}>
              {gradeOverTimeData.length === 0 ? (
                <p style={{ textAlign: 'center' }}>No grade data found</p>
              ) : (
                <ResponsiveContainer width="100%" height="100%">
                  <LineChart data={gradeOverTimeData}>
                    <CartesianGrid stroke="#ccc" strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <Tooltip
                      contentStyle={{
                        backgroundColor: '#fff',
                        border: '1px solid #ccc',
                        borderRadius: '6px'
                      }}
                    />
                    <Line
                      type="monotone"
                      dataKey="grade"
                      stroke="#F97316"
                      strokeWidth={3}
                      dot={{ r: 4 }}
                      activeDot={{ r: 6 }}
                    />
                  </LineChart>
                </ResponsiveContainer>
              )}
            </div>
          </div>

          {/* XP by Path (PieChart) */}
          <div style={styles.chartCard}>
            <h2 style={styles.chartTitle}>XP by Path</h2>
            <div style={{ height: '300px', width: '100%' }}>
              {xpByPathData.length === 0 ? (
                <p style={{ textAlign: 'center' }}>No XP path data found</p>
              ) : (
                <ResponsiveContainer width="100%" height="100%">
                  <PieChart>
                    <Pie
                      data={xpByPathData}
                      dataKey="value"
                      nameKey="name"
                      outerRadius={100}
                      fill="#6366F1"
                      label={({ name, value }) => `${name} (${value})`}
                    >
                      {xpByPathData.map((_entry, i) => (
                        <Cell key={`cell-${i}`} fill={COLORS[i % COLORS.length]} />
                      ))}
                    </Pie>
                    <Tooltip
                      contentStyle={{
                        backgroundColor: '#fff',
                        border: '1px solid #ccc',
                        borderRadius: '6px'
                      }}
                    />
                  </PieChart>
                </ResponsiveContainer>
              )}
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

